<?php	session_start();	if(!isset($_SESSION['id'])) {		echo "<script>window: location='login.php'</script>";	}	$user_log = $_SESSION['username'];?><html>	<head>		<title>Simple Login System</title>				<script type="text/JavaScript">			// Set timeout variables.			var timoutWarning = 840000; // Display warning in 14 Mins.			var timoutNow = 900000; // Timeout in 15 mins.			var logoutUrl = 'logout.php'; // URL to logout page.			var warningTimer;			var timeoutTimer;			// Start timers.			function StartTimers() {				warningTimer = setTimeout("IdleWarning()", timoutWarning);				timeoutTimer = setTimeout("IdleTimeout()", timoutNow);			}			// Reset timers.			function ResetTimers() {				clearTimeout(warningTimer);				clearTimeout(timeoutTimer);				StartTimers();				$("#timeout").dialog('close');			}			// Show idle timeout warning dialog.			function IdleWarning() {			//	$("#timeout").dialog({				//modal: true				alert("Warning, your page will redirected to login page. Due to not move your mouse within the page in 15 minutes.");			//});			}			// Logout the user.			function IdleTimeout() {				window.location = logoutUrl;			}		</script>	</head>	<body onload="StartTimers();" onmousemove="ResetTimers();">		<?php echo "WELCOME USER: $user_log";?> || <a href="logout.php?userlog=<?=$user_log;?>">Logout</a>	</body></html>